import React, { useState } from "react";
import TextField from "@mui/material/TextField";
import Grid from "@mui/material/Grid";
import { Link } from "react-router-dom";
import Button from "@mui/material/Button";

const initialSendData = {
    email: "",
    password: "",
    repeatPassword: "",
    name: "",
    hospital: "",
    tel: "",
    country: "",
    term_check: false,
};

const SignUp = () => {
    const [nameErrors, setNameErrors] = useState(true);

    const [isSuccess, setIsSuccess] = useState(false);

    const validateCredentials = () => {
        // Implement your logic to validate email and password.
        // Assume successful validation and set isSuccess to true.
        setIsSuccess(true);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        validateCredentials();
    };

    const [sendData, setSendData] = useState(initialSendData); // 초기 상태로 설정

    const checkName = (e) => {
        var nameRegExp = /^[가-힣]+$/;
        setNameErrors(!nameRegExp.test(e.target.value));
    };

    const onchange = (e) => {
        setSendData({
            ...sendData,
            [e.target.name]: e.target.value,
        });
    };

    return (
        <div className="SignUp">
            <div className="whole">
                <div className="head">
                    <h2>이름을 입력해주세요</h2>
                </div>
                <form onSubmit={handleSubmit}>
                    <div className="signup_name_texfield">
                        <TextField
                            value={sendData.name}
                            label="이름"
                            required
                            name="name"
                            margin="normal"
                            color="secondary"
                            fullWidth
                            autoFocus
                            error={nameErrors && sendData.name !== ""}
                            onChange={(e) => {
                                onchange(e);
                                checkName(e);
                            }}
                            helperText={
                                nameErrors && sendData.name !== ""
                                    ? "올바른 이름을 입력해주세요."
                                    : null
                            }
                        />
                    </div>

                    <Button
                        className="submit_button"
                        type="submit"
                        fullWidth
                        variant="contained"
                        color="secondary"
                        sx={{ mt: 3, mb: 2 }}
                    >
                        Join
                    </Button>
                </form>
                <Grid className="signup_link">
                    Already have a account? <Link to="/">Sign in</Link>{" "}
                </Grid>
                <footer className="footer">
                    Contact Us : 070-4827-1572 / ecrf@purgobio.com
                </footer>
            </div>
        </div>
    );
};

export default SignUp;
