import bell from "./img/bell.png";
import React from "react";
import { useState } from "react";
import { useCallback } from "react";
const PushAlert = () => {
    const [selectedButton, setSelectedButton] = useState(null);

    const handleButtonClick = useCallback((buttonIndex) => {
        setSelectedButton(buttonIndex);
    }, []);

    return (
        <div className="Target">
            <form className="target_form_container">
                <div className="push_head">
                    <p></p>
                </div>
                <div className="bell_image">
                    <img src={bell}></img>
                </div>

                <h3>분명한 목표가 있으면 달성확률이 높아져요!</h3>
                <div className="button-group">
                    <button
                        type="button"
                        className={`button ${
                            selectedButton === 1 ? "selected" : ""
                        }`}
                        onClick={() => handleButtonClick(1)}
                    >
                        안 할래요
                    </button>
                    <button
                        type="button"
                        className={`button ${
                            selectedButton === 2 ? "selected" : ""
                        }`}
                        onClick={() => handleButtonClick(2)}
                    >
                        좋아요
                    </button>
                </div>
            </form>
        </div>
    );
};

export default PushAlert;
