import React, { useState } from "react";
import TextField from "@mui/material/TextField";

import Button from "@mui/material/Button";

const initialSendData = {
    name: "",
    id: "",
};

const SignUp = () => {
    const [nameErrors, setNameErrors] = useState(true);
    const [idErrors, setIdErrors] = useState(true);
    const [isSuccess, setIsSuccess] = useState(false);

    const validateCredentials = () => {
        // Implement your logic to validate email and password.
        // Assume successful validation and set isSuccess to true.
        setIsSuccess(true);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        validateCredentials();
    };

    const [sendData, setSendData] = useState(initialSendData); // 초기 상태로 설정

    const checkName = (e) => {
        var nameRegExp = /^[가-힣]+$/;
        setNameErrors(!nameRegExp.test(e.target.value));
    };

    const checkEmail = (e) => {
        var emailRegExp = /^[a-z]+[a-z0-9]{5,19}$/;
        setEmailErrors(!emailRegExp.test(e.target.value));
    };
    const onchange = (e) => {
        setSendData({
            ...sendData,
            [e.target.name]: e.target.value,
        });
    };

    return (
        <div className="SignUp">
            <div className="whole">
                <form onSubmit={handleSubmit} className="form_container">
                    <div className="signup_name_texfield">
                        <div className="head">
                            <h2>이름을 입력해주세요</h2>
                        </div>
                        <TextField
                            value={sendData.name}
                            label="이름"
                            required
                            name="name"
                            margin="normal"
                            color="secondary"
                            fullWidth
                            autoFocus
                            error={nameErrors && sendData.name !== ""}
                            onChange={(e) => {
                                onchange(e);
                                checkName(e);
                            }}
                            helperText={
                                nameErrors && sendData.name !== ""
                                    ? "올바른 이름을 입력해주세요."
                                    : null
                            }
                        />
                    </div>

                    <Button
                        className="submit_button"
                        type="submit"
                        fullWidth
                        variant="contained"
                        color="secondary"
                        sx={{ mt: 3, mb: 2 }}
                    >
                        Join
                    </Button>
                </form>
            </div>
        </div>
    );
};

export default SignUp;
