import React, { useState, useCallback } from "react";

const Target = () => {
    const [selectedButton, setSelectedButton] = useState("minutes");

    const handleButtonClick = (value) => {
        setSelectedButton(value);
        console.log(value);
    };
    const movePage = useNavigate();

    function goPushAlert() {
        movePage("/PushAlert");
    }

    return (
        <div className="Target">
            <form className="target_form_container">
                <div className="head">
                    <h2>목표를 설정해주세요</h2>
                    <h3>분명한 목표가 있으면 달성확률이 높아져요!</h3>
                </div>
                <div className="button-group">
                    <button
                        type="button"
                        className={`button ${
                            selectedButton === 1 ? "selected" : ""
                        }`}
                        onClick={() => handleButtonClick(1)}
                    >
                        매일 몇 분
                    </button>
                    <button
                        type="button"
                        className={`button ${
                            selectedButton === 2 ? "selected" : ""
                        }`}
                        onClick={() => handleButtonClick(2)}
                    >
                        매주 몇 분
                    </button>
                    <button
                        type="button"
                        className={`button ${
                            selectedButton === 3 ? "selected" : ""
                        }`}
                        onClick={() => handleButtonClick(3)}
                    >
                        매월 몇 분
                    </button>
                    <button
                        type="button"
                        className={`button ${
                            selectedButton === 4 ? "selected" : ""
                        }`}
                        onClick={() => handleButtonClick(4)}
                    >
                        나중에 할래요
                    </button>
                    <button className="target_submit_btn" onClick={goPushAlert}>
                        확인
                    </button>
                </div>
            </form>
        </div>
    );
};

export default Target;
