import bell from "./img/bell.png";
import React from "react";
import { useState } from "react";
import { useCallback } from "react";
const PushAlert = () => {
    const [selectedButton, setSelectedButton] = useState(true);

    const handleButtonClick = (e) => {
        setSelectedButton(e);
        console.log(e);
    };

    return (
        <div className="PushAlert">
            <form className="push_form_container">
                <div className="push_head_bell_image">
                    <div className="push_head">
                        <h2>푸시 알림을 활성화 해주세요</h2>
                    </div>
                    <div className="bell_image">
                        <img src={bell} alt="Bell Icon" />
                    </div>
                </div>
                <div>
                    <h3>분명한 목표가 있으면 달성확률이 높아져요!</h3>
                </div>
                <div className="button-group">
                    <button
                        value={selectedButton}
                        name="false"
                        type="button"
                        className={`button ${
                            selectedButton === 1 ? "selected" : ""
                        }`}
                        onClick={() => handleButtonClick(1)}
                    >
                        안 할래요
                    </button>
                    <button
                        value={selectedButton}
                        name="true"
                        type="button"
                        className={`button ${
                            selectedButton === 2 ? "selected" : ""
                        }`}
                        onClick={() => handleButtonClick(2)}
                    >
                        좋아요
                    </button>
                </div>
                <button className="push_submit_btn">확인</button>
            </form>
        </div>
    );
};

export default PushAlert;
